{"row":99,"column":0},"action":"remove","lines":["class Computer","  attr_accessor :dictionary, :word, :current_guess, :guessed_letters","  ","  def initialize","    @dictionary = []","    @word = nil","    @current_guess = nil","    @guessed_letters = []","    get_dictionary","  end","  ","  def get_dictionary","    File.foreach('dictionary.txt') do |line|","      @dictionary << line.chomp","    end","  end","  ","  def get_common_words","    File.foreach('most_common.txt') do |line|","      @dictionary << line.chomp","    end","  end","    ","  ","  def get_word","    @word = @dictionary.sample","    @current_guess = \"_\" * @word.length","  end","  ","  def guess_correct?(guess)","    letters = @word.split(//)","    correct = false","    letters.each_with_index do |letter, index|","      if letter == guess","        @current_guess[index] = letter ","        correct = true","      end","    end","    puts @current_guess","    correct","  end","  ","  def get_guess(opponents_feedback)","    puts \"So far, the computer has guessed: #{@guessed_letters.to_s}\"","    eliminate_on_length(opponents_feedback.length)","    eliminate_on_positions(opponents_feedback)","    most_common_letter","  end","  ","  def eliminate_on_length(number)","    @dictionary = @dictionary.select { |word| word.length == number }","  end","  ","  def most_common_letter","    letters = get_unguessed_letters","    frequency = letters.inject(Hash.new(0)) { |freq, letter| freq[letter] += 1; freq }","    guess = letters.max_by { |value| frequency[value] }","    @guessed_letters << guess","    guess","  end","  ","  def get_unguessed_letters","    letters = @dictionary.map { |word| word.split(//).uniq }.flatten","    @guessed_letters.each { |letter| letters.delete(letter) }","    letters","  end","  ","  def eliminate_on_positions(opponents_feedback)","    @dictionary = @dictionary.select do |word|","      position_match?(word, opponents_feedback)","    end","  end","  ","  def position_match?(word, opponents_feedback)","    letters = word.split(//)","    letters.each_with_index do |letter, index|","      if opponents_feedback[index] != \"_\" && opponents_feedback[index] != letter","        return false","      end ","    end ","    true","  end","    ","  ","  def get_random_guess","    ","    guess = (97 + rand(25)).chr","    until !@guessed_letters.include?(guess)","      guess = (97 + rand(25)).chr","    end","    @guessed_letters << guess","    guess","  end","  ","  def won?","    @word == @current_guess","  end","  ","end",""],"id":649}],[{"start":{"row":0,"column":0},"end":{"row":1,"column":0},"action":"remove","lines":["",""],"id":650}]]},"ace":{"folds":[],"scrolltop":0,"scrollleft":0,"selection":{"start":{"row":0,"column":0},"end":{"row":0,"column":0},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":0},"timestamp":1434121498448,"hash":"c95c631ffbb4815799b4b9a800980517c8b4a062"}